CREATE TABLE IF NOT EXISTS public.course
(
    id bigint NOT NULL,
    date_created timestamp(6) without time zone,
    estimated_hours integer,
    last_updated timestamp(6) without time zone,
    title character varying(255) COLLATE pg_catalog."default",
    total_lessons integer,
    is_published boolean DEFAULT false,
    trophy_img_source character varying(255) COLLATE pg_catalog."default",
    CONSTRAINT course_pkey PRIMARY KEY (id)
)

TABLESPACE pg_default;

CREATE TABLE IF NOT EXISTS public.enemy
(
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    img_source character varying(255) COLLATE pg_catalog."default" NOT NULL,
    name character varying(255) COLLATE pg_catalog."default" NOT NULL,
    CONSTRAINT enemy_pkey PRIMARY KEY (id)
)

TABLESPACE pg_default;

CREATE TABLE IF NOT EXISTS public.lesson
(
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    course_id bigint NOT NULL,
    order_index integer NOT NULL,
    tasks_json text COLLATE pg_catalog."default",
    title character varying(255) COLLATE pg_catalog."default" NOT NULL,
    background_img_source character varying(255) COLLATE pg_catalog."default",
    enemy_id bigint,
    has_enemy boolean NOT NULL DEFAULT false,
    CONSTRAINT lesson_pkey PRIMARY KEY (id),
    CONSTRAINT fkjs3c7skmg8bvdddok5lc7s807 FOREIGN KEY (course_id)
        REFERENCES public.course (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION,
    CONSTRAINT fktm30vm7xge052gnveypk7nmtv FOREIGN KEY (enemy_id)
        REFERENCES public.enemy (id) MATCH SIMPLE
        ON UPDATE NO ACTION
        ON DELETE NO ACTION
)

TABLESPACE pg_default;

CREATE TABLE IF NOT EXISTS public.equipment
(
    id bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 9223372036854775807 CACHE 1 ),
    cost integer NOT NULL,
    img_source character varying(255) COLLATE pg_catalog."default" NOT NULL,
    item_number integer,
    name character varying(255) COLLATE pg_catalog."default" NOT NULL,
    type character varying(255) COLLATE pg_catalog."default" NOT NULL,
    hidden boolean NOT NULL DEFAULT true,
    CONSTRAINT equipment_pkey PRIMARY KEY (id),
    CONSTRAINT equipment_type_check CHECK (type::text = ANY (ARRAY['HELM'::character varying, 'ARMOR'::character varying, 'WEAPON'::character varying, 'SHOES'::character varying, 'PANTS'::character varying]::text[]))
)

TABLESPACE pg_default;

CREATE TABLE IF NOT EXISTS public.users
(
    id bigint NOT NULL,
    password character varying(255) COLLATE pg_catalog."default",
    user_login character varying(255) COLLATE pg_catalog."default",
    role character varying(255) COLLATE pg_catalog."default" DEFAULT 'USER'::character varying,
    coins integer DEFAULT 0,
    points integer DEFAULT 0,
    rank integer DEFAULT 0,
    hearts integer DEFAULT 5,
    last_heart_recovery timestamp(6) without time zone,
    is_blocked boolean NOT NULL DEFAULT false,
    CONSTRAINT users_pkey PRIMARY KEY (id)
)

TABLESPACE pg_default;


