INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (20, 202, 5, '{"tasks": [{"id": "task-if-1","type": "TextBox","sentence": "Programy rzadko działają zawsze tak samo. Zazwyczaj muszą podejmować decyzje na podstawie danych. Służy do tego instrukcja ''if'' (jeśli). Pozwala ona wykonać fragment kodu tylko wtedy, gdy spełniony jest konkretny warunek.","bgColor": "bg-blue-50","borderColor": "border-blue-500"},{"id": "task-if-2","type": "TextBox","sentence": "Składnia wygląda tak:\n\nif (wiek >= 18) {\n  System.out.print(\"Jesteś pełnoletni\");\n}\n\nUżywamy tu operatorów porównania: > (większy), < (mniejszy), == (równy), != (różny), >= (większy równy), <= (mniejszy równy).","bgColor": "bg-blue-50","borderColor": "border-blue-500"},{"id": "task-if-3","type": "MultipleChoice","question": "Którego operatora użyjesz, aby sprawdzić, czy dwie liczby są sobie dokładnie równe?","options": ["=","==","==="],"correctAnswer": "=="},{"id": "task-if-4","type": "TextBox","sentence": "A co jeśli warunek nie jest spełniony? Możemy użyć słowa ''else'' (w przeciwnym razie). \n\nif (wynik >= 50) {\n  System.out.print(\"Zdałeś!\");\n} else {\n  System.out.print(\"Spróbuj ponownie.\");\n}","bgColor": "bg-blue-50","borderColor": "border-blue-500"},{"id": "task-if-5","type": "FillInTheBlank","sentence": "Instrukcja, która wykona się, gdy warunek w ''if'' okaże się fałszywy, to [BLANK].","correctAnswers": ["else"]},{"id": "task-if-6","type": "TextBox","sentence": "Możemy też sprawdzać wiele warunków po kolei, używając ''else if''. Program sprawdza je od góry do dołu i wykonuje tylko pierwszy, który jest prawdziwy.\n\nif (punkty > 90) { ... }\nelse if (punkty > 50) { ... }\nelse { ... }","bgColor": "bg-yellow-50","borderColor": "border-yellow-500"},{"id": "task-if-7","type": "MatchingPairs","prompt": "Dopasuj operator do jego znaczenia:","items": [{ "key": "op-1", "left": "!=", "right": "różny od" },{ "key": "op-2", "left": "<=", "right": "mniejszy lub równy" },{ "key": "op-3", "left": "==", "right": "równy" }]},{"id": "task-if-8","type": "CodeFix","instructions": "Popraw kod tak, aby poprawnie sprawdzał warunek w nawiasach (pamiętaj o odpowiednim operatorze porównania).","incorrectCode": "int x = 10;\nif (x = 10) {\n    System.out.print(\"Liczba to 10\");\n}","fixLineStart": 1,"fixCharStart": 6,"fixCharEnd": 7,"correctGapText": "=="},{"id": "task-if-9","type": "OrderableList","prompt": "Ułóż elementy pełnej konstrukcji warunkowej:","initialItems": [{ "id": "o-1", "text": "if (warunek) { ... }" },{ "id": "o-2", "text": "else if (inny warunek) { ... }" },{ "id": "o-3", "text": "else { ... }" }],"correctOrder": [{ "id": "o-1", "text": "if (warunek) { ... }" },{ "id": "o-2", "text": "else if (inny warunek) { ... }" },{ "id": "o-3", "text": "else { ... }" }]},{"id": "task-if-10","type": "TextBox","sentence": "Świetnie! Teraz Twój program potrafi myśleć i reagować na różne sytuacje. W następnej lekcji dowiemy się, jak powtarzać te same czynności wielokrotnie za pomocą PĘTLI!","bgColor": "bg-green-50","borderColor": "border-green-500"}]}', 'Instrukcje warunkowe if/else', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (25, 202, 7, '{"tasks":[{"id":"task-array-1","type":"TextBox","sentence":"Wyobraź sobie, że musisz zapisać oceny 30 uczniów. Tworzenie 30 osobnych zmiennych byłoby koszmarem! Tu z pomocą przychodzą tablice. Tablica to struktura, która pozwala przechowywać wiele wartości tego samego typu w jednej zmiennej.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-array-2","type":"TextBox","sentence":"Tablicę deklarujemy podając typ danych, nawiasy kwadratowe [] oraz nazwę. Przy tworzeniu musimy określić jej rozmiar, którego nie da się już później zmienić.\nPrzykład: int[] oceny = new int[5]; // Tworzy tablicę na 5 liczb całkowitych.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-array-3","type":"MultipleChoice","question":"Czy po utworzeniu tablicy w Javie można zmienić jej rozmiar (liczbę elementów)?","options":["Tak","Nie"],"correctAnswer":"Nie"},{"id":"task-array-4","type":"TextBox","sentence":"Bardzo ważne: Elementy w tablicy są numerowane od 0! Oznacza to, że pierwszy element ma indeks 0, drugi 1, a ostatni w tablicy 5-elementowej ma indeks 4. Dostęp do elementu uzyskujemy tak: oceny[0] = 5;","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-array-5","type":"FillInTheBlank","sentence":"Jeśli mamy tablicę: String[] names = {\"Ania\", \"Bartek\", \"Czarek\"}; \nto pod indeksem [BLANK] znajduje się imię \"Ania\", a pod indeksem 2 znajduje się [BLANK].","correctAnswers":["0","\"Czarek\""]},{"id":"task-array-7","type":"TextBox","sentence":"Aby dowiedzieć się, ile elementów ma tablica, używamy właściwości .length. Pamiętaj, że próba odwołania się do indeksu, który nie istnieje (np. indeks 5 w tablicy 5-elementowej), spowoduje błąd programu (ArrayIndexOutOfBoundsException).","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-array-6","type":"MatchingPairs","prompt":"Dopasuj pojęcia do ich opisu:","items":[{"key":"a-1","left":"Indeks","right":"Numer pozycji elementu (od 0)"},{"key":"a-2","left":"Length","right":"Właściwość zwracająca rozmiar tablicy"},{"key":"a-3","left":"new int[10]","right":"Tworzenie nowej tablicy 10-elementowej"}]},{"id":"task-array-8","type":"CodeFix","instructions":"Popraw błąd w deklaracji tablicy. Pamiętaj o nawiasach kwadratowych przy typie danych.","incorrectCode":"public class Main {\n    public static void main(String[] args) {\n        int  liczby = new int[3];\n        liczby[0] = 10;\n    }\n}","fixLineStart":2,"fixCharStart":11,"fixCharEnd":12,"correctGapText":"[]"},{"id":"task-array-9","type":"TextBox","sentence":"Gratulacje! Wiesz już, jak grupować dane. Tablice są fundamentem dla bardziej złożonych struktur, które poznasz w przyszłości. W kolejnej lekcji dowiemy się, jak tworzyć własne metody, aby nasz kod był bardziej uporządkowany i gotowy do wielokrotnego użytku!","bgColor":"bg-blue-50","borderColor":"border-blue-500"}]}', 'Tablice w Javie', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (21, 202, 6, '{"tasks":[{"id":"task-loops-1","type":"TextBox","sentence":"Programowanie polega na automatyzacji. Jeśli musisz wypisać ''Cześć'' 100 razy, nie kopiujesz kodu 100 razy - używasz pętli! Pętle pozwalają powtarzać blok kodu tak długo, jak spełniony jest określony warunek.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-loops-2","type":"TextBox","sentence":"Najczęściej używaną pętlą jest ''for''. Składa się ona z trzech części:\n1. Start (inicjalizacja): skąd zaczynamy, np. int i = 0\n2. Warunek: jak długo kręcimy pętlą, np. i < 10\n3. Krok (inkrementacja): o ile zmieniamy licznik, np. i++ (czyli o 1)\n\nWszystko zawarte jest w jednym nawiasie tuż za słowem for:\nfor(int i = 0; i < 10; i++)","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-1-1767958942042","type":"TextBox","sentence":"Warto zwrócić chwilę uwagi na i++. Jest to postinkrementacja i wyrażenie to jest równoznaczne wyrażeniu i = i + 1;\nIstnieje także preinkrementacja (++i), która tak samo jak i++ dodaje 1 do zmiennej.\nRóżnica między nimi polega na tym że preinkrementacja zwiększa zmienną przed wykonaniem na niej dalszych działań, a postinkrementacja po.\nPoza inkrementacją jest jeszcze dekrementacja(--i lub i--), która odejmuje 1 od zmiennej.","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-2-1767959394974","type":"TextBox","sentence":"W przypadku pętli for(int i = 0; i < 10; i++) {...} nasza zmienna ''i'' zacznie od 0, program sprawdzi czy i < 10, po czym wykonany zostanie kod ze środka bloku pętli for. Po jego wykonaniu do ''i'' zostanie dodany 1 i ponownie zostanie sprawdzony warunek czy i < 10. Pętla powtarzać będzie się aż i = 10, ponieważ 10 < 10 jest fałszem co kończy pętlę.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-loops-3","type":"MultipleChoice","question":"Ile razy wykona się pętla: for(int i=0; i < 5; i++) { ... } ?","options":["4","5","6"],"correctAnswer":"5"},{"id":"task-1-1767959833896","type":"MultipleChoice","question":"Aby zwiększyć wartość w pętli o 1 po wykonaniu działań na zmiennej użyjemy:","options":["i--","++i","--i","i++"],"correctAnswer":"i++"},{"id":"task-loops-4","type":"TextBox","sentence":"Istnieje też pętla ''while'' (dopóki). Działa ona prościej: powtarza kod tak długo, dopóki warunek w nawiasie jest prawdziwy. Musisz jednak uważać, aby w środku pętli zmienić coś, co w końcu przerwie warunek, inaczej pętla będzie trwać wiecznie!","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-loops-5","type":"CodeFix","instructions":"Uzupełnij warunek pętli while, aby wykonywała się tak długo, aż zmienna ''licznik'' osiągnie wartość 10.","incorrectCode":"int licznik = 0;\nwhile (licznik <  ) {\n    licznik++;\n}","fixLineStart":1,"fixCharStart":17,"fixCharEnd":17,"correctGapText":"10"},{"id":"task-loops-7","type":"MatchingPairs","prompt":"Dopasuj fragment pętli for do jego zadania:","items":[{"key":"lp-1","left":"int i = 0","right":"Ustawienie wartości początkowej"},{"key":"lp-2","left":"i < 10","right":"Warunek kontynuacji pętli"},{"key":"lp-3","left":"i++","right":"Zwiększenie licznika po każdym kroku"}]},{"id":"task-loops-8","type":"TextBox","sentence":"Pętle idealnie nadają się do przeglądania tablic, o których wspominaliśmy przy okazji String[] args. Możemy przejść przez każdy element listy i coś z nim zrobić - np. wypisać wszystkie argumenty startowe programu.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-loops-9","type":"OrderableList","prompt":"Ułóż poprawną strukturę pętli for, która wypisze liczby od 0 do 9:","initialItems":[{"id":"f-1","text":"for (int i = 0; i < 10; i++) {"},{"id":"f-2","text":"  System.out.println(i);"},{"id":"f-3","text":"}"}],"correctOrder":[{"id":"f-1","text":"for (int i = 0; i < 10; i++) {"},{"id":"f-2","text":"  System.out.println(i);"},{"id":"f-3","text":"}"}]},{"id":"task-loops-10","type":"TextBox","sentence":"Dobra robota! Pętle to potężne narzędzie, które na pewno Ci się przyda. W następnej lekcji opowiemy o tablicach.","bgColor":"bg-green-50","borderColor":"border-green-500"}]}', 'Pętle - powtarzanie kodu', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (18, 202, 3, '{"tasks":[{"id":"task-vars-1","type":"TextBox","sentence":"Wiesz już, jakie typy danych oferuje Java. Teraz nauczymy się, jak ich używać w praktyce! Zmienna to taki „pojemnik” na dane, który ma swoją nazwę i typ. Możemy w nim przechowywać informacje i zmieniać je w trakcie działania programu.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-vars-2","type":"TextBox","sentence":"Spójrz na ten przykład:\n\nint punkty = 10;\npunkty = punkty + 5;\n\nNajpierw stworzyliśmy zmienną ''punkty'' i włożyliśmy tam 10. W drugiej linii zwiększyliśmy jej zawartość o 5. Teraz ''punkty'' wynoszą 15!","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-vars-3","type":"MultipleChoice","question":"Jeśli mamy kod: \nint x = 5; \nx = x + 2; \nJaka będzie ostateczna wartość x?","options":["5","2","7"],"correctAnswer":"7"},{"id":"task-vars-4","type":"TextBox","sentence":"Czas rozwiązać zagadkę z pierwszej lekcji: co to jest (String[] args)? \n\nJest to tablica (czyli lista) tekstów, które możemy przekazać do programu w momencie jego uruchomienia. Wyobraź sobie, że uruchamiasz grę i podajesz swoje imię jako parametr - to właśnie trafi do ''args''.","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-vars-5","type":"TextBox","sentence":"Słowo ''String'' oznacza typ danych (tekst), a nawiasy kwadratowe ''[]'' mówią, że to nie jeden tekst, a cała ich lista. Nazwa ''args'' to skrót od angielskiego ''arguments'' (argumenty).","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-vars-6","type":"FillInTheBlank","sentence":"Nawiasy kwadratowe w zapisie String[ ] oznaczają, że mamy do czynienia z [BLANK] elementów, a nie pojedynczą wartością.","correctAnswers":["tablicą"]},{"id":"task-vars-7","type":"TextBox","sentence":"Wewnątrz metody main możemy odwołać się do konkretnego argumentu, używając numeru (indeksu). Uwaga: w programowaniu liczymy od zera! \n\nargs[0] to pierwszy argument,\nargs[1] to drugi argument.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-vars-8","type":"CodeFix","instructions":"Uzupełnij kod tak, aby wypisał pierwszy argument przekazany do programu (z indeksem 0).","incorrectCode":"public class Program {\n    public static void main(String[] args) {\n        System.out.print(args[ ]);\n    }\n}","fixLineStart":2,"fixCharStart":30,"fixCharEnd":30,"correctGapText":"0"},{"id":"task-vars-9","type":"MatchingPairs","prompt":"Połącz pojęcia z ich znaczeniem w kontekście (String[] args):","items":[{"key":"m-1","left":"String","right":"Typ danych przechowywanych w tablicy"},{"key":"m-2","left":"[]","right":"Symbol oznaczający tablicę (listę)"},{"key":"m-3","left":"args","right":"Nazwa zmiennej (skrót od arguments)"}]},{"id":"task-vars-10","type":"TextBox","sentence":"Podsumowując: zmienne pozwalają nam zapamiętywać dane wewnątrz programu, a String[] args pozwala nam „wstrzykiwać” dane z zewnątrz podczas startu aplikacji. To fundamenty interaktywności!","bgColor":"bg-green-50","borderColor":"border-green-500"}]}', 'Zmienne w akcji', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (22, 202, 8, '{"tasks":[{"id":"task-meth-1","type":"TextBox","sentence":"Do tej pory używaliśmy głównie gotowej metody ''main''. Ale w programowaniu chodzi o to, by dzielić kod na mniejsze, reużywalne fragmenty. Takim fragmentem jest właśnie własna metoda.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-meth-2","type":"TextBox","sentence":"Metoda może przyjmować dane (parametry) i zwracać wynik. Jeśli metoda ma coś zwrócić (np. wynik dodawania), musimy określić jej typ (np. int, String) i użyć słowa kluczowego ''return''.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-meth-3","type":"MultipleChoice","question":"Którego słowa kluczowego użyjesz, aby metoda wysłała wynik z powrotem do miejsca, gdzie została wywołana?","options":["give","return","send"],"correctAnswer":"return"},{"id":"task-meth-4","type":"TextBox","sentence":"Jeśli metoda nie zwraca niczego (tylko np. wypisuje tekst na ekran), używamy znanego Ci już słowa ''void''. \n\npublic static void powitanie() {\n  System.out.print(\"Cześć!\");\n}","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-meth-5","type":"FillInTheBlank","sentence":"Metoda, która oblicza pole i zwraca liczbę całkowitą, powinna zacząć się od: public static [BLANK] obliczPole()","correctAnswers":["int"]},{"id":"task-meth-6","type":"CodeFix","instructions":"Popraw metodę tak, aby poprawnie zwracała sumę dwóch liczb.","incorrectCode":"public static int suma(int a, int b) {\n    int s = a + b;\n    s;\n}","fixLineStart":2,"fixCharStart":4,"fixCharEnd":4,"correctGapText":"return"},{"id":"task-meth-7","type":"OrderableList","prompt":"Ułóż elementy definicji metody w poprawnej kolejności:","initialItems":[{"id":"m-1","text":"public static int pomnoz(int a) {"},{"id":"m-2","text":"  return a * 2;"},{"id":"m-3","text":"}"}],"correctOrder":[{"id":"m-1","text":"public static int pomnoz(int a) {"},{"id":"m-2","text":"  return a * 2;"},{"id":"m-3","text":"}"}]},{"id":"task-meth-8","type":"TextBox","sentence":"Brawo! Tworzenie metod pozwala na pisanie czytelnego kodu. Zamiast jednej wielkiej metody main, będziesz mieć wiele małych, z których każda robi jedną rzecz dobrze.","bgColor":"bg-green-50","borderColor":"border-green-500"}]}', 'Własne metody i zwracanie wartości', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (17, 202, 1, '{"tasks":[{"id":"task-1-1765540792976","type":"TextBox","sentence":"Witaj! Oto Twoja pierwsza lekcja z języka programowania Java. Przed tobą długa droga, ale nie martw się, przebrniesz przez nią w mgnieniu oka! Na początek, zacznijmy od tego czym jest Java.","bgColor":"bg-blue-100","borderColor":"border-blue-500"},{"id":"task-2-1765540884567","type":"TextBox","sentence":"Java to obiektowy język programowania. Z tego opisu można już wywnioskować, że skupia się na wykorzystywaniu obiektów do programowania.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-3-1765540982205","type":"MultipleChoice","question":"Jakim językiem programowania jest Java?","options":["Kwadratowym","Obiektowym","Kątowym"],"correctAnswer":"Obiektowym"},{"id":"task-4-1765541045296","type":"TextBox","sentence":"Świetnie! Szybko się uczysz. Przejdźmy więc od razu do pisania pierwszego kodu. Aby tradycji stała się zadość zaczniemy od nauki jak napisać program wypisujący na ekranie \"Hello World!\".","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-5-1765541072384","type":"FillInTheBlank","sentence":"Programiści najczęściej zaczynają naukę programowania w danym języku poprzez napisanie programu wypisującego na ekranie \n\"[BLANK] [BLANK]!\"","correctAnswers":["Hello","World"]},{"id":"task-1-1765541327841","type":"TextBox","sentence":"Oto przykład kodu wypisującego \"Hello World!\":\npublic class Hello {\n  public static void main(String[] args){\n    System.out.print(\"Hello World\");\n  }\n}\nNa początku może on być on trochę przytłaczający, ale spokojnie opiszemy go krok po kroku.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-2-1765541485803","type":"TextBox","sentence":"Zacznijmy od samego początku czyli słowa \"class\". Klasy w programowaniu służą m.in. do opisywania otaczających nas przedmiotów, pojęć, zdarzeń, zdań, itd. W skrócie można opisać klasę jako pojemnik przechowujący i przetwarzający określone informacje. Klasy składają się ze zmiennych i metod. Nasza klasa nazywa się \"Hello\".","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-3-1765541790416","type":"TextBox","sentence":"Utworzony plik musi być nazwany tak samo jak nazwa publicznej klasy. Pamiętaj, aby zarówno nazwa pliku jak i klasy zaczynały się od dużej litery. W javie klasy oznaczane są dużą literą.","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-4-1765541889177","type":"TextBox","sentence":"W naszym wcześniejszym kodzie nie mamy żadnej zmiennej (i dlatego opiszemy je w kolejnej lekcji), ale mamy metodę:\n\npublic static void main(String[] args){\n    System.out.print(\"Hello World\");\n  }\nMetoda to zbiór operacji (zamknięty w blok) jakie mogą być wykonywane przez daną klasę. Zamknięcie określonego kodu w metodzie pozwala na wykorzystanie go wiele razy.\n","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-5-1765542099371","type":"TextBox","sentence":"Nasza metoda w tym przypadku nazywa się \"main\". Metoda \"main\" to główna metoda klasy od której zaczyna się działanie programu i to właśnie w niej wywoływane są inne metody.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-7-1765542214742","type":"MatchingPairs","prompt":"Dopasuj terminy do definicji.","items":[{"key":"pair-1","left":"Klasa","right":"służy do opisywania otaczającego nas świata"},{"key":"pair-2","left":"Metoda","right":"zbiór operacji jakie mogą być wykonywane przez klasę"}]},{"id":"task-8-1765542325132","type":"TextBox","sentence":"Metody mogą zarówno zwracać jakąś wartość jak i przyjmować określoną wartość. Przed naszą metodą \"main\" znajduje się słówko \"void\", które informuje, że metoda niczego nie zwraca. W okrągłych nawiasach za naszą nazwą metody znajdują się wartości jakie metoda przyjmuje, aby móc je przetworzyć. O tym co oznacza \"String[] args\" w nawiasach okrągłych oraz jakie wartości może zwracać i przyjmować metoda opowiemy w kolejnych lekcjach :)","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-9-1765542589168","type":"TextBox","sentence":"Jak można było zauważyć zarówno klasa Hello jak i nasza metoda main były poprzedzone słowem \"public\". Słowo to oznacza, że klasa i metoda są publiczne, czyli że mogą być wykorzystywane poza obrębem pliku w którym zostały utworzone.\nNatomiast słowo \"static\" przy metodzie odnosi się do tego, że metoda jest statyczna. O tym czym jest statyczna metoda opowiemy później.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-10-1765542759824","type":"TextBox","sentence":"Zarówno klasa jak i metoda mają za sobą klamry {}. Klamry te służą do oznaczania, że dany fragment kodu należy do określonej klasy/metody, dlatego przy pisaniu klasy i metody pamiętaj, aby ich kod znajdował się pomiędzy { i }.","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-11-1765542877957","type":"TextBox","sentence":"Opisaliśmy już prawie cały kod poza linijką, w której znajduje się zdanie \"Hello World!\". Przypomnijmy sobie jak wyglądał cały kod:\npublic class Hello {\n  public static void main(String[] args){\n    System.out.print(\"Hello World\");\n  }\n}\nTeraz opiszemy co robi System.out.print","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-12-1765543359272","type":"TextBox","sentence":"W skrócie ujmując, System to biblioteka, która przy użyciu strumienia(w naszym przypadku out czyli wyjścia) pozwala na przekazywanie wartości. W naszym przykładzie System.out.print(\"Hello World!\"); korzystamy ze strumienia, aby wypisać na ekranie/terminalu ciąg znaków będący zdaniem \"Hello World!\". Cała linia zakończona jest znakiem \";\", który oznacza zakończenie danej instrukcji.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-14-1765543691336","type":"CodeFix","instructions":"W poniższym kodzie znajduje się 1 błąd. Popraw ten błąd, aby kod był prawidłowy","incorrectCode":"public class Hello {\n    public static void main(String[] args) {\n        System.out.print(\"Hello World!\"):\n    }\n}","fixLineStart":2,"fixCharStart":40,"fixCharEnd":41,"correctGapText":";"},{"id":"task-1-1767962512990","type":"TextBox","sentence":"Opisaliśmy już teraz cały kod Hello i pokazaliśmy jak można napisać program wypisujący \"Hello World!\" na ekranie. Twoja przygoda z programowaniem w Javie dopiero się zaczyna!","bgColor":"bg-blue-50","borderColor":"border-blue-500"}]}', 'Wstęp do Javy', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (19, 202, 4, '{"tasks":[{"id":"task-math-1","type":"TextBox","sentence":"Skoro potrafisz już tworzyć zmienne, czas zmusić komputer do liczenia! Java radzi sobie z matematyką doskonale. Do podstawowych działań używamy operatorów: + (dodawanie), - (odejmowanie), * (mnożenie) oraz / (dzielenie).","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-math-2","type":"TextBox","sentence":"Wynik działania możemy od razu przypisać do nowej zmiennej:\n\nint wynik = 10 + 5; // wynik to 15\ndouble cenaZRabatem = 100.0 * 0.8; // cena to 80.0","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-math-3","type":"MultipleChoice","question":"Jaki symbol w Javie odpowiada za mnożenie?","options":["x","*","^"],"correctAnswer":"*"},{"id":"task-math-4","type":"TextBox","sentence":"Ważna uwaga przy dzieleniu! Jeśli dzielisz przez siebie dwie liczby całkowite (int), Java odrzuci część ułamkową. \n\nPrzykład: 5 / 2 da wynik 2, a nie 2.5! Aby otrzymać ułamek, co najmniej jedna liczba musi być typu double.","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-1-1767958387749","type":"MultipleChoice","question":"Aby wynik dzielenia był precyzyjny (np. 2.5), użyjemy typu:","options":["int","String","double"],"correctAnswer":"double"},{"id":"task-math-6","type":"TextBox","sentence":"Java zna jeszcze jeden bardzo przydatny operator: % (modulo). Zwraca on resztę z dzielenia.\n\nPrzykład: 10 % 3 to 1, bo 10 mieści trzy trójki i zostaje 1 reszty.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-math-7","type":"MultipleChoice","question":"Ile wynosi wynik operacji: 7 % 2?","options":["3","1","0"],"correctAnswer":"1"},{"id":"task-math-8","type":"OrderableList","prompt":"Ułóż kod obliczający pole prostokąta (bokA * bokB) i zapisujący je w zmiennej ''pole'':","initialItems":[{"id":"i-1","text":"int pole"},{"id":"i-2","text":"="},{"id":"i-3","text":"bokA * bokB"},{"id":"i-4","text":";"}],"correctOrder":[{"id":"i-1","text":"int pole"},{"id":"i-2","text":"="},{"id":"i-3","text":"bokA * bokB"},{"id":"i-4","text":";"}]},{"id":"task-math-9","type":"CodeFix","instructions":"Popraw błąd w obliczeniach, aby równanie poprawnie zwracało resztę z dzielenia:","incorrectCode":"public class Matematyka {\n    public static void main(String[] args) {\n        int reszta= 9 / 4;\n    }\n}","fixLineStart":2,"fixCharStart":22,"fixCharEnd":23,"correctGapText":"%"},{"id":"task-math-10","type":"TextBox","sentence":"Gratulacje! Potrafisz już deklarować zmienne, pobierać argumenty ze String[] args i wykonywać na nich obliczenia. W następnej lekcji nauczymy się, jak sprawiać, by program podejmował decyzje za pomocą instrukcji IF!","bgColor":"bg-green-50","borderColor":"border-green-500"}]}', 'Operacje matematyczne', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (16, 202, 2, '{"tasks":[{"id":"task-types-1","type":"TextBox","sentence":"Świetnie poszło Ci z pierwszym programem! Teraz czas nauczyć się, jak Java przechowuje informacje. W programowaniu używamy do tego ''zmiennych''. Każdą zmienną musimy opatrzyć odpowiednim typem, który mówi komputerowi, jakiego rodzaju dane tam trzymamy.","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-types-2","type":"TextBox","sentence":"Podstawowe typy w Javie to tzw. typy proste (prymitywne). Najważniejsze z nich to:\n- int: liczby całkowite (np. 5, -10, 1000)\n- double: liczby zmiennoprzecinkowe/ułamki (np. 3.14, 0.5)\n- boolean: wartości logiczne (true lub false)\n- char: pojedyncze znaki (np. ''A'', ''!'')","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-types-3","type":"MultipleChoice","question":"Którego typu użyjesz, aby zapisać wiek osoby (liczba całkowita)?","options":["double","int","boolean"],"correctAnswer":"int"},{"id":"task-types-4","type":"MatchingPairs","prompt":"Dopasuj typ danych do przykładowej wartości:","items":[{"key":"p-1","left":"int","right":"42"},{"key":"p-2","left":"double","right":"19.99"},{"key":"p-3","left":"boolean","right":"true"},{"key":"p-4","left":"char","right":"''X''"}]},{"id":"task-types-5","type":"TextBox","sentence":"Oprócz typów prostych, bardzo często używamy typu String. Służy on do przechowywania ciągów znaków (tekstów). W odróżnieniu od char, String zapisujemy w cudzysłowie, a nie w apostrofach.","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-1-1767957325802","type":"TextBox","sentence":"Aby móc korzystać ze zmiennej musimy ją zadeklarować. Wszystko co musimy zrobić to napisać typ jaki chcemy, aby nasza zmienna miała oraz jej nazwę, np.:\nint liczba;\nAby zainicjalizować zmienną musimy przypisać do niej wartość korzystając ze znaku równości(=):\nint liczba = 4;","bgColor":"bg-blue-50","borderColor":"border-blue-500"},{"id":"task-2-1767957521771","type":"TextBox","sentence":"Pamiętaj, wartość jaką przypisujemy do zmiennej musi być taka sama jak typ zmiennej inaczej program będzie pokazywać błędy.","bgColor":"bg-yellow-50","borderColor":"border-yellow-500"},{"id":"task-types-6","type":"FillInTheBlank","sentence":"Aby stworzyć zmienną tekstową o treści ''Java'', napiszemy: [BLANK] mojaZmienna = [BLANK];","correctAnswers":["String","\"Java\""]},{"id":"task-types-7","type":"OrderableList","prompt":"Ułóż elementy instrukcji tworzenia zmiennej (deklaracji i inicjalizacji) w poprawnej kolejności:","initialItems":[{"id":"item-1","text":"int"},{"id":"item-2","text":"liczba"},{"id":"item-3","text":"="},{"id":"item-4","text":"10;"}],"correctOrder":[{"id":"item-1","text":"int"},{"id":"item-2","text":"liczba"},{"id":"item-3","text":"="},{"id":"item-4","text":"10;"}]},{"id":"task-types-8","type":"CodeFix","instructions":"W Javie typ double używa kropki jako separatora dziesiętnego, a nie przecinka. Popraw poniższy kod zwracając uwagę na tę informację:","incorrectCode":"public class Program {\n    public static void main(String[] args) {\n        double cena = 25,99;\n    }\n}","fixLineStart":2,"fixCharStart":24,"fixCharEnd":25,"correctGapText":"."},{"id":"task-types-9","type":"TextBox","sentence":"Pamiętaj: Java jest językiem ''silnie typowanym''. Oznacza to, że raz zadeklarowana zmienna (np. typu int) nie może nagle zacząć przechowywać tekstu (String). Porządek musi być!","bgColor":"bg-blue-50","borderColor":"border-blue-500"}]}', 'Typy danych w Javie', NULL, NULL, false);
INSERT INTO public.lesson (id, course_id, order_index, tasks_json, title, background_img_source, enemy_id, has_enemy) VALUES (23, 202, 9, '{"tasks":[{"id":"test-1","type":"TextBox","sentence":"Czas sprawdzić, ile zapamiętałeś z dotychczasowych lekcji! Przed Tobą finałowe starcie. Powodzenia!","bgColor":"bg-purple-50","borderColor":"border-purple-500"},{"id":"test-2","type":"MultipleChoice","question":"Który typ danych najlepiej nadaje się do przechowywania ceny produktu (np. 19.99)?","options":["int","double","boolean"],"correctAnswer":"double"},{"id":"test-3","type":"MatchingPairs","prompt":"Dopasuj operatory do ich funkcji:","items":[{"key":"t-1","left":"%","right":"Reszta z dzielenia"},{"key":"t-2","left":"==","right":"Sprawdzenie równości"},{"key":"t-3","left":"!","right":"Negacja (zaprzeczenie)"}]},{"id":"test-4","type":"FillInTheBlank","sentence":"Główna metoda, od której zaczyna się każdy program w Javie, nazywa się [BLANK]. Aby metoda mogła być używana poza plikiem, w którym została zadeklarowana musimy użyć słowa [BLANK].","correctAnswers":["main","public"]},{"id":"test-5","type":"MultipleChoice","question":"Co się stanie, jeśli w pętli while warunek będzie zawsze prawdziwy?","options":["Program wypisze błąd","Powstanie pętla nieskończona","Pętla wykona się raz"],"correctAnswer":"Powstanie pętla nieskończona"},{"id":"test-6","type":"CodeFix","instructions":"Popraw deklarację zmiennej tekstowej, aby zmienna imie przyjmowała ciąg znaków \"Jan\".","incorrectCode":"string imie = ''Jan'';","fixLineStart":0,"fixCharStart":14,"fixCharEnd":19,"correctGapText":"\"Jan\""},{"id":"test-7","type":"OrderableList","prompt":"Ułóż kod pętli for wypisującej liczby od 0 do 2:","initialItems":[{"id":"o-1","text":"for(int i=0; i<3; i++) {"},{"id":"o-2","text":"  System.out.println(i);"},{"id":"o-3","text":"}"}],"correctOrder":[{"id":"o-1","text":"for(int i=0; i<3; i++) {"},{"id":"o-2","text":"  System.out.println(i);"},{"id":"o-3","text":"}"}]},{"id":"test-8","type":"FillInTheBlank","sentence":"Tablica argumentów startowych przekazywana do metody main to String[] [BLANK]. Int[] oznacza tablicę przechowującą liczby [BLANK].","correctAnswers":["args","całkowite"]},{"id":"test-9","type":"MultipleChoice","question":"Co oznacza słowo ''void'' przed nazwą metody?","options":["Metoda zwraca liczbę","Metoda nic nie zwraca","Metoda jest tajna"],"correctAnswer":"Metoda nic nie zwraca"},{"id":"test-10","type":"TextBox","sentence":"Gratulacje! Jeśli dotarłeś tutaj, oznacza to, że masz solidne podstawy Javy. Jesteś gotowy na trudniejsze wyzwania, takie jak Programowanie Obiektowe!","bgColor":"bg-green-50","borderColor":"border-green-500"}]}', 'Wielki Test Wiedzy', NULL, 1, true);

